package com.example.android.timelinefragment

import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import android.widget.ImageView
import android.widget.LinearLayout
import android.widget.TextView
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.bumptech.glide.Glide

class TimelineAdapter : RecyclerView.Adapter<TimelineAdapter.MyViewHolder>() {

    val dataList = listOf(
        TimelineDataModel(
            "12 Dec 2020",
            arrayListOf(
                "data:image/jpeg;base64,",
                "data:image/jpeg;base64,",
                "https://cdn2.vectorstock.com/i/1000x1000/57/61/water-can-watering-plant-icon-vector-22665761.jpg"
            ),
            "500"
        ),
        TimelineDataModel(
            "12 Dec 2020",
            arrayListOf(
                "data:image/jpeg;base64,",
                "https://cdn5.vectorstock.com/i/1000x1000/28/94/farming-wheat-symbol-vector-1022894.jpg",
                "https://cdn2.vectorstock.com/i/1000x1000/57/61/water-can-watering-plant-icon-vector-22665761.jpg"
            ),
            "500"
        ),
        TimelineDataModel(
            "12 Dec 2020",
            arrayListOf(
                "data:image/jpeg;base64,",
                "https://cdn5.vectorstock.com/i/1000x1000/28/94/farming-wheat-symbol-vector-1022894.jpg",
                "https://cdn2.vectorstock.com/i/1000x1000/57/61/water-can-watering-plant-icon-vector-22665761.jpg"
            ),
            "500"
        ),
        TimelineDataModel(
            "12 Dec 2020",
            arrayListOf("data:image/jpeg;base64,"),
            "500"
        )
    )

    class MyViewHolder(view: View) : RecyclerView.ViewHolder(view)

    override fun onCreateViewHolder(
        parent: ViewGroup,
        viewType: Int
    ): MyViewHolder {
        val view = LayoutInflater.from(parent.context)
            .inflate(R.layout.layout_timeline_item, parent, false)
        return MyViewHolder(view)
    }

    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {
        if (position >= dataList.size) {
            holder.itemView.findViewById<TextView>(R.id.tvTimelineDate)
                .setTextColor(holder.itemView.context.getColor(R.color.white))
            holder.itemView.findViewById<ImageView>(R.id.ivCircle).visibility = View.INVISIBLE
            holder.itemView.findViewById<View>(R.id.ivLine).visibility = View.INVISIBLE
        } else {
            val currData = dataList[position]
            holder.itemView.findViewById<TextView>(R.id.tvTimelineDate)
                .setTextColor(holder.itemView.context.getColor(R.color.black))
            holder.itemView.findViewById<TextView>(R.id.tvTimelineDate).text = currData.date
            holder.itemView.findViewById<TextView>(R.id.tvTimelineAmount).text = currData.amount
            holder.itemView.findViewById<ImageView>(R.id.ivCircle).visibility = View.VISIBLE
            holder.itemView.findViewById<View>(R.id.ivLine).visibility = View.VISIBLE
            holder.itemView.findViewById<ImagesCustomView>(R.id.llImages).setImages(currData.imageList)
        }
    }

    override fun getItemCount(): Int {
        return (dataList.size + 4)
    }
}